import { useState, useEffect } from 'react';
import { Paper, Typography, Box, List, ListItem, ListItemText, Button, CircularProgress } from '@mui/material';
import { Event, Sync } from '@mui/icons-material';
import api from '../../services/api';

const CalendarView = () => {
    const [events, setEvents] = useState([]);
    const [loading, setLoading] = useState(false);
    const [authUrl, setAuthUrl] = useState(null);

    useEffect(() => {
        // Check if we can get events or if we need auth
        // For this demo, we'll try to get auth URL first if no events
        const checkAuth = async () => {
            try {
                const res = await api.get('/calendar/auth');
                setAuthUrl(res.data.url);
            } catch (e) {
                // console.log("Auth not needed or failed");
            }
        };
        checkAuth();
    }, []);

    const handleSync = async () => {
        setLoading(true);
        // Mock sync
        setTimeout(() => {
            setEvents([
                { id: 1, summary: 'Team Standup', start: { dateTime: new Date().toISOString() } },
                { id: 2, summary: 'Client Sync', start: { dateTime: new Date(Date.now() + 86400000).toISOString() } },
            ]);
            setLoading(false);
        }, 1500);
    };

    return (
        <Paper sx={{ p: 2 }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
                <Typography variant="h6"><Event sx={{ verticalAlign: 'middle', mr: 1 }} /> Calendar</Typography>
                <Button startIcon={<Sync />} onClick={handleSync} disabled={loading}>
                    Sync
                </Button>
            </Box>

            {loading ? <CircularProgress /> : (
                <List dense>
                    {events.length === 0 ? (
                        <Typography variant="body2" color="textSecondary">No upcoming events synced.</Typography>
                    ) : (
                        events.map(ev => (
                            <ListItem key={ev.id}>
                                <ListItemText
                                    primary={ev.summary}
                                    secondary={new Date(ev.start.dateTime).toLocaleString()}
                                />
                            </ListItem>
                        ))
                    )}
                </List>
            )}

            {authUrl && events.length === 0 && (
                <Box sx={{ mt: 2 }}>
                    <Button variant="outlined" size="small" href={authUrl} target="_blank">
                        Connect Google Calendar
                    </Button>
                </Box>
            )}
        </Paper>
    );
};

export default CalendarView;

import { useState } from 'react';
import { Layout, ConfigProvider, theme } from 'antd';
import { Outlet } from 'react-router-dom';
import Sidebar from './Sidebar';
import Header from './Header';
import { useThemeMode } from '../../context/ThemeContext';

const { Content } = Layout;

const DashboardLayout = () => {
    const { mode } = useThemeMode();
    const [mobileSidebarOpen, setMobileSidebarOpen] = useState(false);
    const [desktopCollapsed, setDesktopCollapsed] = useState(false);

    // Ant Design Breakpoints (useGrid hook inside component?)
    // Simpler to use CSS media queries or Grid.useBreakpoint if we want JS control.
    // Let's use a custom hook or Grid.useBreakpoint
    // We can just rely on window width for simplicity in this file or use Grid.useBreakpoint.

    // Using Grid.useBreakpoint requires it to be inside a component.
    // Let's create a wrapper or just use CSS + State.
    // Actually, Layout responds well to CSS.
    // But we need to pass props to Sidebar.

    // Let's assume we pass these down.

    const toggleMobileSidebar = () => setMobileSidebarOpen(!mobileSidebarOpen);
    const toggleDesktopCollapse = () => setDesktopCollapsed(!desktopCollapsed);

    // Dynamic Sider Width
    // Mobile: 0 (Hidden physically, shown via Drawer)
    // Tablet: 80 (Collapsed)
    // Desktop: 240 (Full) or 80 if collapsed manually

    // We'll manage this in styles/classes mostly, but passing props helps Sidebar choose mode.

    const algorithm = mode === 'dark' ? theme.darkAlgorithm : theme.defaultAlgorithm;

    return (
        <ConfigProvider
            theme={{
                algorithm,
                token: {
                    colorPrimary: '#0052CC',
                    fontFamily: 'Roboto, "Helvetica Neue", Arial',
                },
            }}
        >
            <Layout style={{ minHeight: '100vh' }}>
                <Header onMenuClick={toggleMobileSidebar} />
                <Layout style={{ marginTop: 60 }}>
                    <Sidebar
                        mobileOpen={mobileSidebarOpen}
                        setMobileOpen={setMobileSidebarOpen}
                        collapsed={desktopCollapsed}
                        setCollapsed={setDesktopCollapsed}
                    />
                    <Content
                        className="site-layout-content"
                        style={{
                            // Responsive margin handled by CSS usually, but here we can stick to simple logic:
                            // If Mobile: 0. If Tablet/Desktop: 240 or 80.
                            // We will use a CSS class or inline style with media query hook? 
                            // Let's use standard Ant Layout logic where Sider pushes content.
                            // But since Sidebar is `position: fixed`, we must add margin manually.
                            marginLeft: 0, // Will be overridden by responsive logic logic below or component
                            padding: '24px',
                            minHeight: 280,
                            transition: 'all 0.2s'
                        }}
                    >
                        <Outlet />
                    </Content>
                </Layout>
            </Layout>
            <style>{`
                /* Desktop (default) */
                .site-layout-content { margin-left: ${desktopCollapsed ? '80px' : '240px'} !important; }
                
                /* Tablet (768px - 1199px) - Auto collapse if not set? We'll force it via JS logic better */
                
                /* Mobile */
                @media (max-width: 767px) {
                    .site-layout-content { margin-left: 0 !important; padding: 16px !important; }
                }
            `}</style>
        </ConfigProvider>
    );
};

export default DashboardLayout;

import { Layout, Menu, theme, Drawer, Grid } from 'antd';
import {
    ProjectOutlined,
    UnorderedListOutlined, // Board
    CalendarOutlined,
    TeamOutlined,
    FileTextOutlined,
    SettingOutlined,
    DashboardOutlined,
    MessageOutlined, // Chat vs Meetings
    ApartmentOutlined,
    HeartOutlined,
    InboxOutlined,
    BarChartOutlined,
} from '@ant-design/icons';
import { useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import { useState } from 'react';

const { Sider } = Layout;
const { useBreakpoint } = Grid;

const Sidebar = ({ mobileOpen, setMobileOpen, collapsed, setCollapsed }) => {
    const navigate = useNavigate();
    const location = useLocation();
    const { user } = useAuth();
    const screens = useBreakpoint();

    // State for hover-expand on tablet
    const [hoverExpanded, setHoverExpanded] = useState(false);

    const {
        token: { colorBgContainer, colorBorderSecondary },
    } = theme.useToken();

    // Determine current sidebar state based on breakpoints
    const isMobile = !screens.md; // < 768px

    // On tablet (md && !xl), default to collapsed (icon only)
    // On desktop (xl), default to full
    // But we let parent control 'collapsed' state for persistence if needed.
    // Ideally update 'collapsed' based on breakpoint changes, but simple logic here:

    const effectiveCollapsed = isMobile ? false : (collapsed || (!screens.xl && !hoverExpanded));
    const effectiveWidth = isMobile ? 280 : (effectiveCollapsed ? 80 : 240);

    const hasAccess = (roles) => {
        if (!roles || roles.includes('all')) return true;
        return roles.includes(user?.role) || user?.role === 'admin';
    };

    const items = [
        {
            key: 'project-group',
            label: 'PROJECT: PLATFORM',
            type: 'group',
            children: [
                { key: '/dashboard', icon: <DashboardOutlined />, label: 'Summary' },
                { key: '/dashboard/tasks', icon: <UnorderedListOutlined />, label: 'Board' },
                { key: '/dashboard/backlog', icon: <InboxOutlined />, label: 'Backlog' },
                { key: '/dashboard/roadmap', icon: <CalendarOutlined />, label: 'Roadmap' },
                { key: '/dashboard/reports', icon: <BarChartOutlined />, label: 'Reports' },
            ]
        },
        { type: 'divider' },
        {
            key: 'global-group',
            label: 'APPS',
            type: 'group',
            children: [
                { key: '/dashboard/meetings', icon: <MessageOutlined />, label: 'Meetings', hidden: !hasAccess(['project_manager', 'technical_lead']) },
                { key: '/dashboard/documents', icon: <FileTextOutlined />, label: 'Documents' },
                { key: '/dashboard/chat', icon: <MessageOutlined />, label: 'Chat' },
                { key: '/dashboard/organization', icon: <ApartmentOutlined />, label: 'Team' },
                { key: '/dashboard/wellness', icon: <HeartOutlined />, label: 'Wellness' },
            ].filter(item => !item.hidden)
        },
        { type: 'divider' },
        { key: '/dashboard/settings', icon: <SettingOutlined />, label: 'Settings' }
    ];

    const MenuContent = (
        <Menu
            mode="inline"
            defaultSelectedKeys={[location.pathname]}
            selectedKeys={[location.pathname]}
            style={{ borderRight: 0, height: '100%' }}
            items={items}
            onClick={({ key }) => {
                navigate(key);
                if (isMobile) setMobileOpen(false);
            }}
        />
    );

    // Mobile Drawer
    if (isMobile) {
        return (
            <Drawer
                placement="left"
                onClose={() => setMobileOpen(false)}
                open={mobileOpen}
                width={280}
                bodyStyle={{ padding: 0 }}
                headerStyle={{ display: 'none' }} // Hide mock header in drawer
            >
                <div style={{ padding: '24px 24px 0', marginBottom: 24 }}>
                    <div style={{ fontSize: 18, fontWeight: 'bold', color: '#0052CC' }}>Digital Dockers</div>
                </div>
                {MenuContent}
            </Drawer>
        );
    }

    // Desktop/Tablet Sider
    return (
        <Sider
            trigger={null}
            collapsible
            collapsed={effectiveCollapsed && !hoverExpanded}
            width={240}
            collapsedWidth={80}
            onMouseEnter={() => !screens.xl && setHoverExpanded(true)}
            onMouseLeave={() => setHoverExpanded(false)}
            style={{
                background: colorBgContainer,
                borderRight: `1px solid ${colorBorderSecondary}`,
                overflowX: 'hidden',
                overflowY: 'auto',
                height: 'calc(100vh - 60px)',
                position: 'fixed',
                left: 0,
                top: 60,
                zIndex: 900,
                transition: 'all 0.2s'
            }}
        >
            {MenuContent}
        </Sider>
    );
};

import { useState, useEffect } from 'react';
import {
    Typography, Grid, Paper, Box, Card, CardContent, Chip, Button,
    Table, TableBody, TableCell, TableContainer, TableHead, TableRow,
    FormControl, Select, MenuItem, Avatar, IconButton, CircularProgress
} from '@mui/material';
import {
    People, Assignment, TrendingUp, EventNote, Add,
    Edit, Save, Cancel, MoreVert, Refresh
} from '@mui/icons-material';
import api from '../../services/api';
import GlassCard from '../common/GlassCard';
import CalendarWidget from '../calendar/CalendarWidget';
import EmailListWidget from '../email/EmailListWidget';
import { toast } from 'react-toastify';
import { format } from 'date-fns';

const ROLES = [
    { value: 'admin', label: 'Admin' },
    { value: 'project_manager', label: 'Project Manager' },
    { value: 'technical_lead', label: 'Technical Lead' },
    { value: 'marketing_lead', label: 'Marketing Lead' },
    { value: 'technical_team', label: 'Technical Team' },
    { value: 'marketing_team', label: 'Marketing Team' }
];

const AdminDashboard = () => {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [editingUserId, setEditingUserId] = useState(null);
    const [selectedRole, setSelectedRole] = useState('');

    const [stats, setStats] = useState({
        totalUsers: 0,
        activeProjects: 24, // Placeholder
        systemHealth: '98%',
        meetingsToday: 18 // Placeholder
    });

    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = async () => {
        try {
            const res = await api.get('/users');
            setUsers(res.data);
            setStats(prev => ({ ...prev, totalUsers: res.data.length }));
        } catch (error) {
            console.error('Failed to fetch users', error);
            // toast.error('Failed to load users');
        } finally {
            setLoading(false);
        }
    };

    const handleEditClick = (user) => {
        setEditingUserId(user._id);
        setSelectedRole(user.role);
    };

    const handleCancelEdit = () => {
        setEditingUserId(null);
        setSelectedRole('');
    };

    const handleSaveRole = async (userId) => {
        try {
            await api.put(`/users/${userId}/role`, { role: selectedRole });

            setUsers(users.map(user =>
                user._id === userId ? { ...user, role: selectedRole } : user
            ));

            toast.success('User role updated successfully');
            setEditingUserId(null);
        } catch (error) {
            toast.error('Failed to update role');
        }
    };

    const getRoleLabel = (roleValue) => {
        const role = ROLES.find(r => r.value === roleValue);
        return role ? role.label : roleValue;
    };

    const getRoleColor = (role) => {
        if (role.includes('admin')) return 'error';
        if (role.includes('manager')) return 'warning';
        if (role.includes('lead')) return 'secondary';
        return 'primary';
    };

    const statCards = [
        { label: 'Total Users', value: stats.totalUsers, icon: <People />, color: '#0052CC', change: '+12' },
        { label: 'Active Projects', value: stats.activeProjects, icon: <Assignment />, color: '#00875A', change: '+3' },
        { label: 'System Health', value: stats.systemHealth, icon: <TrendingUp />, color: '#6554C0', change: '+2%' },
        { label: 'Meetings Today', value: stats.meetingsToday, icon: <EventNote />, color: '#FF991F', change: '+5' },
    ];

    return (
        <Box>
            <Box sx={{ mb: 3, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <Box>
                    <Typography variant="h4" sx={{ fontWeight: 600, mb: 0.5 }}>
                        Admin Dashboard
                    </Typography>
                    <Typography variant="body2" color="text.secondary">
                        System overview and user management
                    </Typography>
                </Box>
                <Box sx={{ display: 'flex', gap: 2 }}>
                    <Button variant="outlined" startIcon={<Refresh />} onClick={fetchUsers}>
                        Refresh
                    </Button>
                    <Button variant="contained" startIcon={<Add />}>
                        Add User
                    </Button>
                </Box>
            </Box>

            {/* Stats Cards */}
            <Grid container spacing={2} sx={{ mb: 3 }}>
                {statCards.map((stat, index) => (
                    <Grid item xs={12} sm={6} md={3} key={index}>
                        <GlassCard
                            sx={{
                                height: '100%',
                                border: '1px solid',
                                borderColor: 'divider',
                                transition: 'all 0.2s',
                                '&:hover': {
                                    borderColor: stat.color,
                                    boxShadow: `0 0 0 1px ${stat.color}`,
                                }
                            }}
                        >
                            <CardContent>
                                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', mb: 1 }}>
                                    <Box sx={{ color: stat.color }}>
                                        {stat.icon}
                                    </Box>
                                    <Chip
                                        label={stat.change}
                                        size="small"
                                        sx={{
                                            height: 20,
                                            fontSize: '0.75rem',
                                            bgcolor: `${stat.color}15`,
                                            color: stat.color,
                                            fontWeight: 600,
                                        }}
                                    />
                                </Box>
                                <Typography variant="h4" sx={{ fontWeight: 600, mb: 0.5 }}>
                                    {stat.value}
                                </Typography>
                                <Typography variant="body2" color="text.secondary">
                                    {stat.label}
                                </Typography>
                            </CardContent>
                        </GlassCard>
                    </Grid>
                ))}
            </Grid>



            {/* Main Content Areas */}
            <Grid container spacing={3}>
                {/* Calendar Widget Column */}
                <Grid item xs={12} md={4}>
                    <Grid container spacing={3} direction="column">
                        <Grid item>
                            <CalendarWidget />
                        </Grid>
                        <Grid item>
                            <EmailListWidget />
                        </Grid>
                    </Grid>
                </Grid>

                {/* User Table Column */}
                <Grid item xs={12} md={8}>
                    {/* User Management Table */}
                    <Paper elevation={0} sx={{ border: '1px solid', borderColor: 'divider', overflow: 'hidden' }}>
                        <Box sx={{ p: 2, borderBottom: '1px solid', borderColor: 'divider', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                            <Typography variant="h6" sx={{ fontWeight: 600 }}>
                                All Users
                            </Typography>
                        </Box>

                        {loading ? (
                            <Box sx={{ p: 4, display: 'flex', justifyContent: 'center' }}>
                                <CircularProgress />
                            </Box>
                        ) : (
                            <TableContainer sx={{ maxHeight: 600 }}>
                                <Table stickyHeader>
                                    <TableHead>
                                        <TableRow>
                                            <TableCell>User</TableCell>
                                            <TableCell>Email</TableCell>
                                            <TableCell>Role / Position</TableCell>
                                            <TableCell>Status</TableCell>
                                            <TableCell>Joined Date</TableCell>
                                            <TableCell align="right">Actions</TableCell>
                                        </TableRow>
                                    </TableHead>
                                    <TableBody>
                                        {users.map((user) => (
                                            <TableRow key={user._id} hover>
                                                <TableCell>
                                                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                                                        <Avatar sx={{ width: 32, height: 32, bgcolor: 'primary.main', fontSize: 14 }}>
                                                            {user.fullName ? user.fullName[0] : user.email[0]}
                                                        </Avatar>
                                                        <Typography variant="body2" fontWeight={500}>
                                                            {user.fullName || 'No Name'}
                                                        </Typography>
                                                    </Box>
                                                </TableCell>
                                                <TableCell>{user.email}</TableCell>
                                                <TableCell>
                                                    {editingUserId === user._id ? (
                                                        <FormControl size="small" fullWidth>
                                                            <Select
                                                                value={selectedRole}
                                                                onChange={(e) => setSelectedRole(e.target.value)}
                                                            >
                                                                {ROLES.map(role => (
                                                                    <MenuItem key={role.value} value={role.value}>
                                                                        {role.label}
                                                                    </MenuItem>
                                                                ))}
                                                            </Select>
                                                        </FormControl>
                                                    ) : (
                                                        <Chip
                                                            label={getRoleLabel(user.role)}
                                                            size="small"
                                                            color={getRoleColor(user.role)}
                                                            variant="outlined"
                                                        />
                                                    )}
                                                </TableCell>
                                                <TableCell>
                                                    <Chip
                                                        label="Active"
                                                        size="small"
                                                        color="success"
                                                        sx={{ height: 24, fontSize: '0.75rem' }}
                                                    />
                                                </TableCell>
                                                <TableCell>
                                                    {format(new Date(user.createdAt || Date.now()), 'MMM d, yyyy')}
                                                </TableCell>
                                                <TableCell align="right">
                                                    {editingUserId === user._id ? (
                                                        <Box sx={{ display: 'flex', justifyContent: 'flex-end', gap: 1 }}>
                                                            <IconButton size="small" color="primary" onClick={() => handleSaveRole(user._id)}>
                                                                <Save />
                                                            </IconButton>
                                                            <IconButton size="small" color="error" onClick={handleCancelEdit}>
                                                                <Cancel />
                                                            </IconButton>
                                                        </Box>
                                                    ) : (
                                                        <Button
                                                            size="small"
                                                            startIcon={<Edit />}
                                                            onClick={() => handleEditClick(user)}
                                                        >
                                                            Edit Role
                                                        </Button>
                                                        // <IconButton size="small" onClick={() => handleEditClick(user)}>
                                                        //     <Edit fontSize="small" />
                                                        // </IconButton>
                                                    )}
                                                </TableCell>
                                            </TableRow>
                                        ))}
                                    </TableBody>
                                </Table>
                            </TableContainer>
                        )}
                    </Paper>
                </Grid>
            </Grid>
        </Box>
    );
};

export default AdminDashboard;

import { useAuth } from '../../context/AuthContext';
import ProjectDashboard from './ProjectDashboard';

const DashboardHome = () => {
    // For now, everyone lands on the new Project Dashboard to see the Jira Redesign
    return <ProjectDashboard />;
};

export default DashboardHome;

import { useState, useEffect } from 'react';
import {
    Box, Drawer, Typography, IconButton, TextField, Button,
    CircularProgress, Divider, Paper
} from '@mui/material';
import { Close, Send, AutoAwesome } from '@mui/icons-material';
import ReactMarkdown from 'react-markdown'; // Assuming react-markdown is installed or just standard text
import api from '../../services/api';

const DocumentAnalysisDrawer = ({ open, onClose, documentId, documentTitle }) => {
    const [loading, setLoading] = useState(false);
    const [summary, setSummary] = useState('');
    const [chatHistory, setChatHistory] = useState([]);
    const [question, setQuestion] = useState('');
    const [chatLoading, setChatLoading] = useState(false);

    useEffect(() => {
        if (open && documentId) {
            fetchAnalysis();
        } else {
            // Reset state on close
            setSummary('');
            setChatHistory([]);
            setQuestion('');
        }
    }, [open, documentId]);

    const fetchAnalysis = async () => {
        setLoading(true);
        try {
            const res = await api.post(`/rag/analyze/${documentId}`);
            setSummary(res.data.summary);
            setChatHistory([]); // Reset chat on new analysis or load
        } catch (error) {
            console.error(error);
            setSummary('Failed to analyze document.');
        } finally {
            setLoading(false);
        }
    };

    const handleSend = async () => {
        if (!question.trim()) return;

        const currentQ = question;
        setQuestion('');
        setChatHistory(prev => [...prev, { role: 'user', content: currentQ }]);
        setChatLoading(true);

        try {
            const res = await api.post(`/rag/chat/${documentId}`, { question: currentQ });
            setChatHistory(prev => [...prev, { role: 'assistant', content: res.data.answer }]);
        } catch (error) {
            setChatHistory(prev => [...prev, { role: 'assistant', content: 'Error getting answer.' }]);
        } finally {
            setChatLoading(false);
        }
    };

    return (
        <Drawer
            anchor="right"
            open={open}
            onClose={onClose}
            PaperProps={{ sx: { width: { xs: '100%', md: 500 }, p: 3 } }}
        >
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
                <Typography variant="h6" fontWeight={700}>
                    AI Analysis: {documentTitle}
                </Typography>
                <IconButton onClick={onClose}><Close /></IconButton>
            </Box>

            <Divider sx={{ mb: 2 }} />

            {loading ? (
                <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
                    <CircularProgress />
                    <Typography sx={{ ml: 2 }}>Analyzing Document...</Typography>
                </Box>
            ) : (
                <Box sx={{ flexGrow: 1, display: 'flex', flexDirection: 'column', overflow: 'hidden' }}>

                    {/* Summary Section */}
                    <Box sx={{ mb: 4, overflow: 'auto', maxHeight: '40vh' }}>
                        <Box sx={{ display: 'flex', alignItems: 'center', mb: 1, color: '#4f46e5' }}>
                            <AutoAwesome sx={{ mr: 1 }} />
                            <Typography variant="subtitle1" fontWeight={600}>Summary</Typography>
                        </Box>
                        <Paper sx={{ p: 2, bgcolor: 'background.default', borderRadius: 2 }}>
                            <Typography variant="body2" sx={{ whiteSpace: 'pre-wrap' }}>
                                {summary || "No summary available."}
                            </Typography>
                        </Paper>
                    </Box>

                    <Divider sx={{ mb: 2 }} />

                    {/* Chat Section */}
                    <Typography variant="subtitle1" fontWeight={600} gutterBottom>
                        Ask a Question
                    </Typography>

                    <Box sx={{ flexGrow: 1, overflow: 'auto', mb: 2, display: 'flex', flexDirection: 'column', gap: 2 }}>
                        {chatHistory.map((msg, i) => (
                            <Box
                                key={i}
                                sx={{
                                    alignSelf: msg.role === 'user' ? 'flex-end' : 'flex-start',
                                    bgcolor: msg.role === 'user' ? 'primary.main' : 'rgba(0,0,0,0.05)',
                                    color: msg.role === 'user' ? 'white' : 'text.primary',
                                    p: 1.5,
                                    borderRadius: 2,
                                    maxWidth: '85%'
                                }}
                            >
                                <Typography variant="body2">{msg.content}</Typography>
                            </Box>
                        ))}
                        {chatLoading && (
                            <Box sx={{ alignSelf: 'flex-start', bgcolor: 'rgba(0,0,0,0.05)', p: 1.5, borderRadius: 2 }}>
                                <Typography variant="body2" fontStyle="italic">Thinking...</Typography>
                            </Box>
                        )}
                    </Box>

                    {/* Input */}
                    <Box sx={{ display: 'flex', gap: 1 }}>
                        <TextField
                            fullWidth
                            size="small"
                            placeholder="Ask something about the document..."
                            value={question}
                            onChange={(e) => setQuestion(e.target.value)}
                            onKeyPress={(e) => e.key === 'Enter' && handleSend()}
                            disabled={chatLoading}
                        />
                        <Button
                            variant="contained"
                            onClick={handleSend}
                            disabled={chatLoading || !question.trim()}
                        >
                            <Send fontSize="small" />
                        </Button>
                    </Box>

                </Box>
            )}
        </Drawer>
    );
};

export default DocumentAnalysisDrawer;

