import { useState, useEffect } from 'react';
import {
    Box, Grid, Paper, Typography, Button, Table, TableBody,
    TableCell, TableContainer, TableHead, TableRow, Chip, IconButton
} from '@mui/material';
import { UploadFile, Visibility, AutoAwesome } from '@mui/icons-material';
import api from '../../services/api';
import { toast } from 'react-toastify';
import DocumentAnalysisDrawer from './DocumentAnalysisDrawer';

const DocumentManager = () => {
    const [documents, setDocuments] = useState([]);
    const [analysisOpen, setAnalysisOpen] = useState(false);
    const [selectedDoc, setSelectedDoc] = useState(null);

    const fetchDocuments = async () => {
        try {
            const res = await api.get('/documents');
            setDocuments(res.data);
        } catch (error) {
            console.error(error);
        }
    };

    useEffect(() => {
        fetchDocuments();
    }, []);

    const handleFileUpload = async (e) => {
        const file = e.target.files[0];
        if (!file) return;

        const formData = new FormData();
        formData.append('file', file);
        formData.append('type', 'contract');

        try {
            // Send with multipart/form-data (axios auto-sets this for FormData)
            const res = await api.post('/documents/upload', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });
            toast.success('Document uploaded successfully');
            fetchDocuments();
        } catch (error) {
            console.error('Upload error:', error);
            toast.error(error.response?.data?.message || 'Upload failed');
        }
    };

    const handleAnalyze = (doc) => {
        setSelectedDoc(doc);
        setAnalysisOpen(true);
    };

    const handleCloseAnalysis = () => {
        setAnalysisOpen(false);
        setSelectedDoc(null);
    };

    return (
        <Box>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', mb: 3 }}>
                <Typography variant="h5">Document Vault</Typography>
                <Button variant="contained" component="label" startIcon={<UploadFile />}>
                    Upload Document
                    <input type="file" hidden onChange={handleFileUpload} />
                </Button>
            </Box>

            <TableContainer component={Paper}>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell>Title</TableCell>
                            <TableCell>Type</TableCell>
                            <TableCell>Status</TableCell>
                            <TableCell>Actions</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {documents.map((doc) => (
                            <TableRow key={doc._id}>
                                <TableCell>{doc.title}</TableCell>
                                <TableCell>{doc.type}</TableCell>
                                <TableCell>
                                    <Chip label={doc.status} size="small" />
                                </TableCell>
                                <TableCell>
                                    <Button
                                        size="small"
                                        startIcon={<Visibility />}
                                        sx={{ mr: 1 }}
                                        href={`http://localhost:5000/${doc.file?.filepath}`} // Simple link for now
                                        target="_blank"
                                    >
                                        View
                                    </Button>
                                    <Button
                                        size="small"
                                        startIcon={<AutoAwesome />}
                                        color="secondary"
                                        onClick={() => handleAnalyze(doc)}
                                    >
                                        Analyze AI
                                    </Button>
                                </TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>

            {selectedDoc && (
                <DocumentAnalysisDrawer
                    open={analysisOpen}
                    onClose={handleCloseAnalysis}
                    documentId={selectedDoc._id}
                    documentTitle={selectedDoc.title}
                />
            )}
        </Box>
    );
};

export default DocumentManager;

import { useState, useEffect } from 'react';
import {
    List, ListItem, ListItemText, ListItemAvatar, Avatar,
    Typography, Paper, CircularProgress, Chip, Box, Button
} from '@mui/material';
import { VideoCall, Schedule, Add } from '@mui/icons-material';
import { format, isPast, isFuture } from 'date-fns';
import { useNavigate } from 'react-router-dom';
import api from '../../services/api';
import { useAuth } from '../../context/AuthContext';

const MeetingList = ({ limit = 5 }) => {
    const [meetings, setMeetings] = useState([]);
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();
    const { user } = useAuth();

    const canScheduleMeetings = ['admin', 'project_manager', 'technical_lead', 'marketing_lead'].includes(user?.role);

    useEffect(() => {
        const fetchMeetings = async () => {
            try {
                const res = await api.get('/meetings');
                setMeetings(Array.isArray(res.data) ? res.data.slice(0, limit) : []);
            } catch (error) {
                console.error(error);
                setMeetings([]);
            } finally {
                setLoading(false);
            }
        };
        fetchMeetings();
    }, [limit]);

    if (loading) {
        return (
            <Box sx={{ p: 3, textAlign: 'center' }}>
                <CircularProgress size={24} />
            </Box>
        );
    }

    if (!meetings || meetings.length === 0) {
        return (
            <Box sx={{ p: 3, textAlign: 'center' }}>
                <VideoCall sx={{ fontSize: 40, color: 'text.secondary', mb: 1 }} />
                <Typography variant="body2" color="text.secondary">
                    No meetings yet
                </Typography>
                {canScheduleMeetings && (
                    <Button
                        size="small"
                        sx={{ mt: 1 }}
                        onClick={() => navigate('/dashboard/meetings')}
                    >
                        Schedule a meeting
                    </Button>
                )}
            </Box>
        );
    }

    return (
        <Box>
            <List sx={{ p: 0 }}>
                {meetings.map((meeting, index) => {
                    const isUpcoming = isFuture(new Date(meeting.scheduledAt));
                    return (
                        <ListItem
                            key={meeting._id}
                            sx={{
                                borderBottom: index < meetings.length - 1 ? '1px solid' : 'none',
                                borderColor: 'divider',
                                '&:hover': { bgcolor: 'action.hover' }
                            }}
                            secondaryAction={
                                <Chip
                                    label={isUpcoming ? 'Upcoming' : 'Past'}
                                    size="small"
                                    color={isUpcoming ? 'primary' : 'default'}
                                    variant="outlined"
                                />
                            }
                        >
                            <ListItemAvatar>
                                <Avatar sx={{ bgcolor: isUpcoming ? '#0052CC' : 'grey.400' }}>
                                    <VideoCall />
                                </Avatar>
                            </ListItemAvatar>
                            <ListItemText
                                primary={meeting.title}
                                secondary={
                                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 0.5 }}>
                                        <Schedule fontSize="inherit" />
                                        {format(new Date(meeting.scheduledAt), 'MMM d, h:mm a')}
                                    </Box>
                                }
                                primaryTypographyProps={{ fontWeight: 500 }}
                            />
                        </ListItem>
                    );
                })}
            </List>
            <Box sx={{ p: 1, borderTop: '1px solid', borderColor: 'divider' }}>
                <Button
                    fullWidth
                    size="small"
                    onClick={() => navigate('/dashboard/meetings')}
                >
                    View All Meetings
                </Button>
            </Box>
        </Box>
    );
};

export default MeetingList;

import { useState, useEffect } from 'react';
import {
    Box, Typography, Paper, Tabs, Tab, Button, Card, CardContent,
    Chip, IconButton, Dialog, DialogTitle, DialogContent, DialogActions,
    TextField, Avatar, CircularProgress
} from '@mui/material';
import {
    Add, VideoCall, Schedule, Person, ContentCopy,
    Description, PlayArrow, ExpandMore, ExpandLess, Warning, Link as LinkIcon, Settings
} from '@mui/icons-material';
import { format, isPast, isFuture } from 'date-fns';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import { toast } from 'react-toastify';
import ScheduleMeetingModal from './ScheduleMeetingModal';
import { useNavigate } from 'react-router-dom';

const MeetingsPage = () => {
    const { user } = useAuth();
    const [meetings, setMeetings] = useState([]);
    const [loading, setLoading] = useState(true);
    const [activeTab, setActiveTab] = useState(0);
    const [scheduleModalOpen, setScheduleModalOpen] = useState(false);
    const [selectedMeeting, setSelectedMeeting] = useState(null);
    const [transcriptDialogOpen, setTranscriptDialogOpen] = useState(false);
    const [transcriptText, setTranscriptText] = useState('');
    const [expandedMeeting, setExpandedMeeting] = useState(null);
    const [googleCalendarConnected, setGoogleCalendarConnected] = useState(false);
    const navigate = useNavigate();

    const canScheduleMeetings = ['admin', 'project_manager', 'technical_lead', 'marketing_lead'].includes(user?.role);

    useEffect(() => {
        fetchMeetings();
        // Check if user has Google Calendar connected
        setGoogleCalendarConnected(!!user?.googleAccessToken);
    }, [user]);

    const fetchMeetings = async () => {
        try {
            const res = await api.get('/meetings');
            setMeetings(res.data || []);
        } catch (error) {
            console.error('Error fetching meetings:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleMeetingCreated = (newMeeting) => {
        setMeetings([newMeeting, ...meetings]);
    };

    const handleCopyLink = (link) => {
        navigator.clipboard.writeText(link);
        toast.success('Meeting link copied!');
    };

    const handleJoinMeeting = (link, isPlaceholder) => {
        if (isPlaceholder) {
            toast.warning('This is a placeholder link. Connect Google Calendar in settings to get real meeting links!');
            return;
        }
        window.open(link, '_blank');
    };

    // Check if meeting link is a placeholder
    const isPlaceholderLink = (meetLink) => {
        // Placeholder links don't have calendarEventId, or start with random characters
        return meetLink && !meetLink.includes('?authuser') && meetLink.match(/^https:\/\/meet\.google\.com\/[a-z]{3}-[a-z]{3}-[a-z]{3}$/);
    };

    const handleAddTranscript = async () => {
        if (!transcriptText.trim()) {
            toast.error('Please enter transcript text');
            return;
        }

        try {
            await api.post('/meetings/' + selectedMeeting._id + '/transcript', { text: transcriptText });
            toast.success('Transcript added successfully!');
            setTranscriptDialogOpen(false);
            setTranscriptText('');
            fetchMeetings();
        } catch (error) {
            toast.error('Failed to add transcript');
        }
    };

    const upcomingMeetings = meetings.filter(m =>
        isFuture(new Date(m.scheduledAt)) && m.status !== 'cancelled'
    );
    const pastMeetings = meetings.filter(m =>
        isPast(new Date(m.scheduledAt)) || m.status === 'completed'
    );

    const MeetingCard = ({ meeting, isPastMeeting }) => {
        const isExpanded = expandedMeeting === meeting._id;

        return (
            <Card elevation={0} sx={{ border: '1px solid', borderColor: 'divider', mb: 2, '&:hover': { borderColor: 'primary.main' } }}>
                <CardContent>
                    <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
                        <Box sx={{ flex: 1 }}>
                            <Typography variant="h6" sx={{ fontWeight: 600, mb: 1 }}>
                                {meeting.title}
                            </Typography>
                            <Box sx={{ display: 'flex', alignItems: 'center', gap: 2, mb: 1 }}>
                                <Box sx={{ display: 'flex', alignItems: 'center', gap: 0.5 }}>
                                    <Schedule fontSize="small" color="action" />
                                    <Typography variant="body2" color="text.secondary">
                                        {format(new Date(meeting.scheduledAt), 'PPP p')}
                                    </Typography>
                                </Box>
                                <Chip label={meeting.duration + ' min'} size="small" variant="outlined" />
                                <Chip
                                    label={meeting.status}
                                    size="small"
                                    color={meeting.status === 'completed' ? 'success' : meeting.status === 'scheduled' ? 'primary' : 'default'}
                                />
                            </Box>
                            {meeting.description && (
                                <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
                                    {meeting.description}
                                </Typography>
                            )}
                        </Box>
                        <Box sx={{ display: 'flex', gap: 1 }}>
                            {!isPastMeeting && meeting.meetLink && (
                                <>
                                    {isPlaceholderLink(meeting.meetLink) ? (
                                        <>
                                            <Button
                                                variant="outlined"
                                                size="small"
                                                startIcon={<Warning />}
                                                onClick={() => {
                                                    toast.info(
                                                        <div>
                                                            <div>This is a placeholder link that won't work.</div>
                                                            <div style={{ marginTop: 8 }}>
                                                                <IconButton
                                                                    color="warning"
                                                                    size="small"
                                                                    title="Go to Settings to connect Google Calendar"
                                                                    onClick={() => navigate('/dashboard/settings')}
                                                                >
                                                                    <Settings fontSize="small" />
                                                                </IconButton>
                                                            </div>
                                                        </div>,
                                                        { autoClose: 5000 }
                                                    );
                                                }}
                                                color="warning"
                                            >
                                                Invalid Link
                                            </Button>
                                        </>
                                    ) : (
                                        <>
                                            <Button variant="contained" size="small" startIcon={<PlayArrow />} onClick={() => handleJoinMeeting(meeting.meetLink, false)}>
                                                Join
                                            </Button>
                                            <IconButton size="small" onClick={() => handleCopyLink(meeting.meetLink)}>
                                                <ContentCopy fontSize="small" />
                                            </IconButton>
                                        </>
                                    )}
                                </>
                            )}
                            {isPastMeeting && !meeting.transcript?.text && canScheduleMeetings && (
                                <Button variant="outlined" size="small" startIcon={<Description />} onClick={() => { setSelectedMeeting(meeting); setTranscriptDialogOpen(true); }}>
                                    Add Transcript
                                </Button>
                            )}
                            <IconButton size="small" onClick={() => setExpandedMeeting(isExpanded ? null : meeting._id)}>
                                {isExpanded ? <ExpandLess /> : <ExpandMore />}
                            </IconButton>
                        </Box>
                    </Box>

                    {meeting.participants?.length > 0 && (
                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mt: 1 }}>
                            <Person fontSize="small" color="action" />
                            <Box sx={{ display: 'flex', gap: 0.5, flexWrap: 'wrap' }}>
                                {meeting.participants.slice(0, 3).map((p, idx) => (
                                    <Chip key={idx} label={p.name || p.email} size="small" variant="outlined" />
                                ))}
                                {meeting.participants.length > 3 && (
                                    <Chip label={'+' + (meeting.participants.length - 3) + ' more'} size="small" />
                                )}
                            </Box>
                        </Box>
                    )}

                    {isExpanded && meeting.transcript?.text && (
                        <Box sx={{ mt: 2, pt: 2, borderTop: '1px solid', borderColor: 'divider' }}>
                            <Typography variant="subtitle2" sx={{ mb: 1, fontWeight: 600 }}>Transcript</Typography>
                            <Paper variant="outlined" sx={{ p: 2, maxHeight: 300, overflow: 'auto', bgcolor: 'background.default' }}>
                                <Typography variant="body2" sx={{ whiteSpace: 'pre-wrap' }}>
                                    {meeting.transcript.text}
                                </Typography>
                            </Paper>
                        </Box>
                    )}

                    {isExpanded && meeting.meetLink && (
                        <Box sx={{ mt: 2, pt: 2, borderTop: '1px solid', borderColor: 'divider' }}>
                            <Typography variant="subtitle2" sx={{ mb: 1, fontWeight: 600 }}>Meeting Link</Typography>
                            {isPlaceholderLink(meeting.meetLink) ? (
                                <Box sx={{
                                    p: 2,
                                    bgcolor: 'warning.light',
                                    borderRadius: 1,
                                    border: '1px solid',
                                    borderColor: 'warning.main'
                                }}>
                                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 1 }}>
                                        <Warning color="warning" />
                                        <Typography variant="body2" sx={{ fontWeight: 600 }}>Placeholder Link</Typography>
                                    </Box>
                                    <Typography variant="caption" sx={{ display: 'block', mb: 1 }}>
                                        This link won't work. Connect Google Calendar in Settings to generate real Google Meet links.
                                    </Typography>
                                    <Typography variant="caption" sx={{ display: 'block', color: 'text.secondary', fontFamily: 'monospace' }}>
                                        {meeting.meetLink}
                                    </Typography>
                                    <Button
                                        variant="outlined"
                                        size="small"
                                        startIcon={<Settings />}
                                        onClick={() => navigate('/dashboard/settings')}
                                        sx={{ mt: 1 }}
                                    >
                                        Go to Settings
                                    </Button>
                                </Box>
                            ) : (
                                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                                    <VideoCall color="primary" />
                                    <Typography variant="body2" sx={{ color: 'primary.main', cursor: 'pointer' }} onClick={() => handleJoinMeeting(meeting.meetLink, false)}>
                                        {meeting.meetLink}
                                    </Typography>
                                </Box>
                            )}
                        </Box>
                    )}
                </CardContent>
            </Card>
        );
    };

    if (loading) {
        return (
            <Box sx={{ display: 'flex', justifyContent: 'center', py: 4 }}>
                <CircularProgress />
            </Box>
        );
    }

    return (
        <Box>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
                <Box>
                    <Typography variant="h4" sx={{ fontWeight: 600 }}>Meetings</Typography>
                    <Typography variant="body2" color="text.secondary">Schedule, join, and review meeting transcripts</Typography>
                </Box>
                {canScheduleMeetings && (
                    <Button variant="contained" startIcon={<Add />} onClick={() => setScheduleModalOpen(true)}>
                        Schedule Meeting
                    </Button>
                )}
            </Box>

            <Paper elevation={0} sx={{ border: '1px solid', borderColor: 'divider' }}>
                <Tabs value={activeTab} onChange={(e, v) => setActiveTab(v)} sx={{ borderBottom: 1, borderColor: 'divider', px: 2 }}>
                    <Tab label={'Upcoming (' + upcomingMeetings.length + ')'} />
                    <Tab label={'Past Meetings (' + pastMeetings.length + ')'} />
                </Tabs>

                <Box sx={{ p: 3 }}>
                    {activeTab === 0 && (
                        upcomingMeetings.length === 0 ? (
                            <Box sx={{ textAlign: 'center', py: 4 }}>
                                <VideoCall sx={{ fontSize: 48, color: 'text.secondary', mb: 1 }} />
                                <Typography color="text.secondary">No upcoming meetings</Typography>
                                {canScheduleMeetings && (
                                    <Button sx={{ mt: 2 }} onClick={() => setScheduleModalOpen(true)}>Schedule your first meeting</Button>
                                )}
                            </Box>
                        ) : (
                            upcomingMeetings.map(meeting => (
                                <MeetingCard key={meeting._id} meeting={meeting} isPastMeeting={false} />
                            ))
                        )
                    )}

                    {activeTab === 1 && (
                        pastMeetings.length === 0 ? (
                            <Box sx={{ textAlign: 'center', py: 4 }}>
                                <Description sx={{ fontSize: 48, color: 'text.secondary', mb: 1 }} />
                                <Typography color="text.secondary">No past meetings</Typography>
                            </Box>
                        ) : (
                            pastMeetings.map(meeting => (
                                <MeetingCard key={meeting._id} meeting={meeting} isPastMeeting={true} />
                            ))
                        )
                    )}
                </Box>
            </Paper>

            <ScheduleMeetingModal open={scheduleModalOpen} onClose={() => setScheduleModalOpen(false)} onMeetingCreated={handleMeetingCreated} />

            <Dialog open={transcriptDialogOpen} onClose={() => setTranscriptDialogOpen(false)} maxWidth="md" fullWidth>
                <DialogTitle>Add Meeting Transcript</DialogTitle>
                <DialogContent>
                    <Typography variant="body2" color="text.secondary" sx={{ mb: 2 }}>
                        Paste the meeting transcript below.
                    </Typography>
                    <TextField fullWidth multiline rows={10} placeholder="Paste transcript here..." value={transcriptText} onChange={(e) => setTranscriptText(e.target.value)} />
                </DialogContent>
                <DialogActions>
                    <Button onClick={() => setTranscriptDialogOpen(false)}>Cancel</Button>
                    <Button variant="contained" onClick={handleAddTranscript}>Save Transcript</Button>
                </DialogActions>
            </Dialog>
        </Box>
    );
};

export default MeetingsPage;

import { useState, useEffect } from 'react';
import {
    Dialog, DialogTitle, DialogContent, DialogActions,
    TextField, Button, Box, Typography, Chip, IconButton,
    FormControl, InputLabel, Select, MenuItem, Alert
} from '@mui/material';
import { Close, Add, Person, Schedule } from '@mui/icons-material';
import { DateTimePicker } from '@mui/x-date-pickers/DateTimePicker';
import { LocalizationProvider } from '@mui/x-date-pickers/LocalizationProvider';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';
import api from '../../services/api';
import { toast } from 'react-toastify';

const ScheduleMeetingModal = ({ open, onClose, onMeetingCreated }) => {
    const [formData, setFormData] = useState({
        title: '',
        description: '',
        scheduledAt: new Date(Date.now() + 3600000),
        duration: 60,
        meetingType: 'google_meet'
    });
    const [participants, setParticipants] = useState([]);
    const [participantEmail, setParticipantEmail] = useState('');
    const [loading, setLoading] = useState(false);

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleAddParticipant = () => {
        if (participantEmail && !participants.find(p => p.email === participantEmail)) {
            setParticipants([...participants, { email: participantEmail, name: participantEmail }]);
            setParticipantEmail('');
        }
    };

    const handleRemoveParticipant = (email) => {
        setParticipants(participants.filter(p => p.email !== email));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);

        try {
            const meetingData = {
                ...formData,
                participants: participants.map(p => ({ email: p.email, name: p.name }))
            };

            const res = await api.post('/meetings/schedule', meetingData);
            toast.success('Meeting scheduled successfully!');
            onMeetingCreated && onMeetingCreated(res.data);
            handleClose();
        } catch (error) {
            toast.error(error.response?.data?.message || 'Failed to schedule meeting');
        } finally {
            setLoading(false);
        }
    };

    const handleClose = () => {
        setFormData({
            title: '',
            description: '',
            scheduledAt: new Date(Date.now() + 3600000),
            duration: 60,
            meetingType: 'google_meet'
        });
        setParticipants([]);
        setParticipantEmail('');
        onClose();
    };

    return (
        <Dialog open={open} onClose={handleClose} maxWidth="md" fullWidth>
            <DialogTitle sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                    <Schedule color="primary" />
                    <Typography variant="h6">Schedule Meeting</Typography>
                </Box>
                <IconButton onClick={handleClose} size="small">
                    <Close />
                </IconButton>
            </DialogTitle>

            <form onSubmit={handleSubmit}>
                <DialogContent dividers>
                    <Box sx={{ display: 'flex', flexDirection: 'column', gap: 3 }}>
                        <TextField
                            fullWidth
                            label="Meeting Title"
                            name="title"
                            value={formData.title}
                            onChange={handleChange}
                            required
                            placeholder="e.g., Sprint Planning, Team Standup"
                        />

                        <TextField
                            fullWidth
                            label="Description"
                            name="description"
                            value={formData.description}
                            onChange={handleChange}
                            multiline
                            rows={3}
                            placeholder="Meeting agenda and details..."
                        />

                        <Box sx={{ display: 'flex', gap: 2 }}>
                            <Box sx={{ flex: 1 }}>
                                <LocalizationProvider dateAdapter={AdapterDateFns}>
                                    <DateTimePicker
                                        label="Date & Time"
                                        value={formData.scheduledAt}
                                        onChange={(newValue) => setFormData({ ...formData, scheduledAt: newValue })}
                                        slotProps={{ textField: { fullWidth: true } }}
                                        minDateTime={new Date()}
                                    />
                                </LocalizationProvider>
                            </Box>
                            <FormControl sx={{ minWidth: 150 }}>
                                <InputLabel>Duration</InputLabel>
                                <Select
                                    name="duration"
                                    value={formData.duration}
                                    onChange={handleChange}
                                    label="Duration"
                                >
                                    <MenuItem value={15}>15 minutes</MenuItem>
                                    <MenuItem value={30}>30 minutes</MenuItem>
                                    <MenuItem value={45}>45 minutes</MenuItem>
                                    <MenuItem value={60}>1 hour</MenuItem>
                                    <MenuItem value={90}>1.5 hours</MenuItem>
                                    <MenuItem value={120}>2 hours</MenuItem>
                                </Select>
                            </FormControl>
                        </Box>

                        <Box>
                            <Typography variant="subtitle2" sx={{ mb: 1 }}>Participants</Typography>
                            <Box sx={{ display: 'flex', gap: 1, mb: 2 }}>
                                <TextField
                                    size="small"
                                    placeholder="Enter email address"
                                    value={participantEmail}
                                    onChange={(e) => setParticipantEmail(e.target.value)}
                                    onKeyPress={(e) => { if (e.key === 'Enter') { e.preventDefault(); handleAddParticipant(); } }}
                                    sx={{ flex: 1 }}
                                />
                                <Button variant="outlined" onClick={handleAddParticipant} startIcon={<Add />}>
                                    Add
                                </Button>
                            </Box>
                            <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 1 }}>
                                {participants.map((p, idx) => (
                                    <Chip
                                        key={idx}
                                        icon={<Person />}
                                        label={p.email}
                                        onDelete={() => handleRemoveParticipant(p.email)}
                                        size="small"
                                    />
                                ))}
                                {participants.length === 0 && (
                                    <Typography variant="body2" color="text.secondary">
                                        No participants added yet
                                    </Typography>
                                )}
                            </Box>
                        </Box>

                        <Alert severity="info">
                            A Google Meet link will be automatically generated for this meeting.
                        </Alert>
                    </Box>
                </DialogContent>

                <DialogActions sx={{ p: 2 }}>
                    <Button onClick={handleClose} disabled={loading}>Cancel</Button>
                    <Button type="submit" variant="contained" disabled={loading || !formData.title}>
                        {loading ? 'Scheduling...' : 'Schedule Meeting'}
                    </Button>
                </DialogActions>
            </form>
        </Dialog>
    );
};

export default ScheduleMeetingModal;

import { useCallback, useEffect, useState } from 'react';
import ReactFlow, {
    MiniMap,
    Controls,
    Background,
    useNodesState,
    useEdgesState,
    addEdge,
    Position
} from 'reactflow';
import 'reactflow/dist/style.css';
import dagre from 'dagre';
import { Box, Paper, Typography, Avatar, Chip, useTheme } from '@mui/material';
import api from '../../services/api';
import GlassCard from '../common/GlassCard';

const nodeWidth = 200;
const nodeHeight = 90;

const getLayoutedElements = (nodes, edges, direction = 'TB') => {
    const dagreGraph = new dagre.graphlib.Graph();
    dagreGraph.setDefaultEdgeLabel(() => ({}));

    dagreGraph.setGraph({ rankdir: direction });

    nodes.forEach((node) => {
        dagreGraph.setNode(node.id, { width: nodeWidth, height: nodeHeight });
    });

    edges.forEach((edge) => {
        dagreGraph.setEdge(edge.source, edge.target);
    });

    dagre.layout(dagreGraph);

    nodes.forEach((node) => {
        const nodeWithPosition = dagreGraph.node(node.id);
        node.targetPosition = isHorizontal ? Position.Left : Position.Top;
        node.sourcePosition = isHorizontal ? Position.Right : Position.Bottom;

        // We are shifting the dagre node position (anchor=center center) to the top left
        // so it matches the React Flow node anchor point (top left).
        node.position = {
            x: nodeWithPosition.x - nodeWidth / 2,
            y: nodeWithPosition.y - nodeHeight / 2,
        };

        return node;
    });

    return { nodes, edges };
};

const isHorizontal = false;

const CustomNode = ({ data }) => {
    return (
        <GlassCard
            sx={{
                p: 2,
                width: nodeWidth,
                display: 'flex',
                alignItems: 'center',
                gap: 2,
                borderRadius: 4,
                border: '1px solid',
                borderColor: 'rgba(255,255,255,0.4)',
                background: 'linear-gradient(135deg, rgba(255,255,255,0.9) 0%, rgba(255,255,255,0.6) 100%)',
                boxShadow: '0 8px 32px rgba(31, 38, 135, 0.15)',
                backdropFilter: 'blur(12px)',
                transition: 'all 0.3s cubic-bezier(0.25, 0.8, 0.25, 1)',
                '&:hover': {
                    transform: 'translateY(-5px) scale(1.02)',
                    boxShadow: '0 20px 40px rgba(0,0,0,0.2)',
                    borderColor: 'primary.main'
                }
            }}
        >
            <Avatar
                src={data.avatar}
                sx={{
                    width: 52,
                    height: 52,
                    bgcolor: 'primary.main',
                    border: '3px solid white',
                    boxShadow: 2
                }}
            >
                {data.fullName[0]}
            </Avatar>
            <Box sx={{ overflow: 'hidden' }}>
                <Typography variant="subtitle1" sx={{ fontWeight: 700, fontSize: '0.95rem', lineHeight: 1.2, color: 'text.primary', whiteSpace: 'nowrap', overflow: 'hidden', textOverflow: 'ellipsis' }}>
                    {data.fullName}
                </Typography>
                <Chip
                    label={data.role.replace('_', ' ')}
                    size="small"
                    sx={{
                        mt: 0.6,
                        height: 22,
                        fontSize: '0.65rem',
                        fontWeight: 700,
                        backgroundImage: 'linear-gradient(to right, #4f46e5, #818cf8)',
                        color: 'white',
                        textTransform: 'uppercase',
                        border: 'none',
                        '& .MuiChip-label': { px: 1 }
                    }}
                />
            </Box>
        </GlassCard>
    );
};

const nodeTypes = {
    custom: CustomNode,
};

const OrgGraph = () => {
    const [nodes, setNodes, onNodesChange] = useNodesState([]);
    const [edges, setEdges, onEdgesChange] = useEdgesState([]);
    const theme = useTheme();

    useEffect(() => {
        const fetchOrgTree = async () => {
            try {
                const res = await api.get('/users/org-tree'); // Returns flat list of users
                const users = res.data;

                // Transform to Elements
                const newNodes = users.map(u => ({
                    id: u._id,
                    type: 'custom',
                    data: {
                        fullName: u.fullName,
                        role: u.role,
                        avatar: u.profileInfo?.avatar
                    },
                    position: { x: 0, y: 0 } // Layout handles this
                }));

                const newEdges = users
                    .filter(u => u.reportsTo)
                    .map(u => ({
                        id: `e-${u.reportsTo}-${u._id}`,
                        source: u.reportsTo,
                        target: u._id,
                        type: 'smoothstep',
                        animated: true,
                        style: { stroke: theme.palette.text.secondary, strokeWidth: 2 }
                    }));

                const layouted = getLayoutedElements(newNodes, newEdges);
                setNodes(layouted.nodes);
                setEdges(layouted.edges);

            } catch (err) {
                console.error("Failed to fetch org tree", err);
            }
        };

        fetchOrgTree();
    }, [setNodes, setEdges, theme]);

    const onConnect = useCallback((params) => setEdges((eds) => addEdge(params, eds)), [setEdges]);

    return (
        <Box sx={{ height: 'calc(100vh - 100px)', width: '100%' }}>
            <Typography variant="h5" sx={{ mb: 2, fontWeight: 600 }}>Organization Structure</Typography>
            <Paper sx={{ height: '100%', width: '100%', borderRadius: 3, overflow: 'hidden', border: '1px solid', borderColor: 'divider' }}>
                <ReactFlow
                    nodes={nodes}
                    edges={edges}
                    onNodesChange={onNodesChange}
                    onEdgesChange={onEdgesChange}
                    onConnect={onConnect}
                    nodeTypes={nodeTypes}
                    fitView
                    attributionPosition="bottom-right"
                >
                    <Controls />
                    <MiniMap style={{ borderRadius: 16 }} />
                    <Background variant="dots" gap={12} size={1} />
                </ReactFlow>
            </Paper>
        </Box>
    );
};

export default OrgGraph;

import { useState, useEffect } from 'react';
import { DragDropContext, Droppable, Draggable } from '@hello-pangea/dnd';
import { Card, Typography, Tag, Avatar, Space, Button, Empty, message, Select, Segmented, Grid } from 'antd';
import { useProject } from '../../context/ProjectContext';
import taskService from '../../services/taskService';
import IssueDetailDrawer from '../work/IssueDetailDrawer';

const { Text, Title } = Typography;

const COLUMNS = {
    'todo': { title: 'To Do', status: 'todo' },
    'in_progress': { title: 'In Progress', status: 'in_progress' },
    'review': { title: 'In Review', status: 'review' },
    'done': { title: 'Done', status: 'done' }
};

const TaskBoard = () => {
    const { currentProject, activeSprint } = useProject();
    const [issues, setIssues] = useState([]);
    const [loading, setLoading] = useState(false);
    const [selectedIssue, setSelectedIssue] = useState(null);
    const [boardData, setBoardData] = useState({});

    // Mobile View State
    const screens = Grid.useBreakpoint();
    const isMobile = !screens.md;
    const [mobileStatusFilter, setMobileStatusFilter] = useState('todo');

    useEffect(() => {
        if (currentProject && activeSprint) {
            loadSprintIssues();
        } else {
            setIssues([]);
            setBoardData({});
        }
    }, [currentProject, activeSprint]);

    const loadSprintIssues = async () => {
        setLoading(true);
        try {
            const data = await taskService.getTasks({
                projectId: currentProject._id,
                sprintId: activeSprint._id
            });
            setIssues(data);
            groupIssuesByStatus(data);
        } catch (error) {
            console.error("Failed to load board issues", error);
        } finally {
            setLoading(false);
        }
    };

    const groupIssuesByStatus = (taskList) => {
        const grouped = {
            'todo': [],
            'in_progress': [],
            'review': [],
            'done': []
        };

        taskList.forEach(task => {
            const status = task.status || 'todo';
            if (grouped[status]) {
                grouped[status].push(task);
            } else {
                // Fallback for unknown status
                grouped['todo'].push(task);
            }
        });
        setBoardData(grouped);
    };

    const handleDragEnd = async (result) => {
        const { destination, source, draggableId } = result;

        if (!destination) return;
        if (
            destination.droppableId === source.droppableId &&
            destination.index === source.index
        ) return;

        const startStatus = source.droppableId;
        const finishStatus = destination.droppableId; // This is the status key (e.g., 'in_progress')

        // Optimistic UI Update
        const newBoardData = { ...boardData };

        // Remove from source
        const [movedIssue] = newBoardData[startStatus].splice(source.index, 1);
        // Add to destination
        newBoardData[finishStatus].splice(destination.index, 0, movedIssue);

        setBoardData(newBoardData);

        // API Call
        try {
            await taskService.updateTask(draggableId, { status: finishStatus });
            message.success(`moved to ${COLUMNS[finishStatus].title}`);
        } catch (error) {
            message.error('Failed to update status');
            loadSprintIssues(); // Revert
        }
    };

    if (!currentProject) return <Empty description="Select a Project" />;
    if (!activeSprint) return <Empty description="No Active Sprint" />;

    return (
        <div style={{ padding: isMobile ? '16px' : '0 24px', height: '100%' }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: 24, alignItems: 'center' }}>
                <div>
                    <Title level={isMobile ? 4 : 3}>{activeSprint.name}</Title>
                    <Text type="secondary">{currentProject.name} Board</Text>
                </div>
                {!isMobile && (
                    <Space>
                        <Select defaultValue="all" style={{ width: 120 }} options={[{ value: 'all', label: 'All Issues' }]} />
                        <Button>Validations</Button>
                    </Space>
                )}
            </div>

            {isMobile ? (
                // MOBILE VIEW: Segmented Control + List
                <div style={{ display: 'flex', flexDirection: 'column', gap: 16 }}>
                    <Segmented
                        block
                        options={Object.keys(COLUMNS).map(key => ({
                            label: COLUMNS[key].title,
                            value: key
                        }))}
                        value={mobileStatusFilter}
                        onChange={setMobileStatusFilter}
                    />

                    <div style={{ display: 'flex', flexDirection: 'column', gap: 8 }}>
                        {(boardData[mobileStatusFilter] || []).map(issue => (
                            <Card
                                key={issue._id}
                                onClick={() => setSelectedIssue(issue)}
                                bodyStyle={{ padding: 12 }}
                                bordered={true}
                            >
                                <Space direction="vertical" size={4} style={{ width: '100%' }}>
                                    <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                                        <Text strong>{issue.title}</Text>
                                        <Tag color="blue">{issue.key}</Tag>
                                    </div>
                                    <Space>
                                        {/* Safe Access to priority */}
                                        <Tag color={issue.priority === 'high' ? 'red' : 'orange'}>{issue.priority || 'Medium'}</Tag>
                                        <Avatar size="small" style={{ backgroundColor: '#0052CC' }}>{issue.assignedTo?.[0]?.fullName?.[0] || 'U'}</Avatar>
                                    </Space>
                                </Space>
                            </Card>
                        ))}
                        {(boardData[mobileStatusFilter] || []).length === 0 && <Empty image={Empty.PRESENTED_IMAGE_SIMPLE} description="No issues" />}
                    </div>
                </div>
            ) : (
                // DESKTOP VIEW: Drag & Drop Columns
                <DragDropContext onDragEnd={handleDragEnd}>
                    <div style={{ display: 'flex', gap: 24, overflowX: 'auto', height: 'calc(100vh - 200px)' }}>
                        {Object.keys(COLUMNS).map(columnId => (
                            <div key={columnId} style={{ minWidth: 280, width: 300, display: 'flex', flexDirection: 'column' }}>
                                <div style={{ padding: '8px 4px', textTransform: 'uppercase', fontWeight: 600, color: '#5E6C84', fontSize: 12 }}>
                                    {COLUMNS[columnId].title} {(boardData[columnId] || []).length}
                                </div>
                                <Droppable droppableId={columnId}>
                                    {(provided, snapshot) => (
                                        <div
                                            {...provided.droppableProps}
                                            ref={provided.innerRef}
                                            style={{
                                                background: snapshot.isDraggingOver ? '#e3f2fd' : '#f4f5f7',
                                                padding: 8,
                                                flexGrow: 1,
                                                borderRadius: 4,
                                                overflowY: 'auto'
                                            }}
                                        >
                                            {(boardData[columnId] || []).map((issue, index) => (
                                                <Draggable key={issue._id} draggableId={issue._id} index={index}>
                                                    {(provided, snapshot) => (
                                                        <div
                                                            ref={provided.innerRef}
                                                            {...provided.draggableProps}
                                                            {...provided.dragHandleProps}
                                                            onClick={() => setSelectedIssue(issue)}
                                                            style={{
                                                                userSelect: 'none',
                                                                padding: 16,
                                                                marginBottom: 8,
                                                                background: 'white',
                                                                borderRadius: 2,
                                                                boxShadow: snapshot.isDragging ? '0 5px 10px rgba(0,0,0,0.15)' : '0 1px 2px rgba(0,0,0,0.1)',
                                                                ...provided.draggableProps.style
                                                            }}
                                                        >
                                                            <Space direction="vertical" size={4} style={{ width: '100%' }}>
                                                                <Text>{issue.title}</Text>
                                                                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                                                                    <Space>
                                                                        <Tag color="blue">{issue.issueType.substring(0, 1).toUpperCase()}</Tag>
                                                                        <Text type="secondary" style={{ fontSize: 12 }}>{issue.key}</Text>
                                                                    </Space>
                                                                    <Space>
                                                                        {issue.priority === 'high' && <Tag color="red">High</Tag>}
                                                                        <Avatar size="small" style={{ fontSize: 10, backgroundColor: '#87d068' }}>
                                                                            {issue.assignedTo?.[0]?.fullName?.[0] || 'U'}
                                                                        </Avatar>
                                                                    </Space>
                                                                </div>
                                                            </Space>
                                                        </div>
                                                    )}
                                                </Draggable>
                                            ))}
                                            {provided.placeholder}
                                        </div>
                                    )}
                                </Droppable>
                            </div>
                        ))}
                    </div>
                </DragDropContext>
            )}

            <IssueDetailDrawer
                open={!!selectedIssue}
                issue={selectedIssue}
                onClose={() => {
                    setSelectedIssue(null);
                    loadSprintIssues(); // Refresh to catch any edit details
                }}
            />
        </div>
    );
};

export default TaskBoard;

