import { useState, useEffect } from 'react';
import { DragDropContext, Droppable, Draggable } from '@hello-pangea/dnd';
import { Card, Button, Typography, Tag, Avatar, Space, Modal, theme, Empty, Input, message } from 'antd';
import { EllipsisOutlined } from '@ant-design/icons';
import { useProject } from '../../context/ProjectContext';
import taskService from '../../services/taskService';
import sprintService from '../../services/sprintService';
import IssueDetailDrawer from './IssueDetailDrawer';

const { Text, Title } = Typography;

const Backlog = () => {
    const { currentProject, sprints, refreshBoard } = useProject();
    const [backlogIssues, setBacklogIssues] = useState([]);
    const [selectedIssue, setSelectedIssue] = useState(null);
    const [isCreateSprintModalOpen, setIsCreateSprintModalOpen] = useState(false);
    const [allTasks, setAllTasks] = useState([]);

    // Sprint Form
    const [sprintName, setSprintName] = useState('');
    const [sprintGoal, setSprintGoal] = useState('');

    const {
        token: { colorBgContainer },
    } = theme.useToken();

    useEffect(() => {
        if (currentProject) {
            loadAllTasks();
        }
    }, [currentProject]);

    // Derived state update when allTasks or sprints change
    useEffect(() => {
        // Filter strictly for backlog (no sprint assigned)
        setBacklogIssues(allTasks.filter(t => !t.sprint));
    }, [allTasks, sprints]);

    const loadAllTasks = async () => {
        try {
            const data = await taskService.getTasks({ projectId: currentProject._id });
            setAllTasks(data);
        } catch (error) {
            // console.error("Failed to load tasks", error);
        }
    };

    const handleCreateSprint = async () => {
        if (!sprintName) return;
        try {
            await sprintService.createSprint({
                name: sprintName,
                goal: sprintGoal,
                projectId: currentProject._id
            });
            message.success('Sprint created');
            setIsCreateSprintModalOpen(false);
            setSprintName('');
            setSprintGoal('');
            refreshBoard(); // Refresh sprints in context
        } catch (error) {
            message.error('Failed to create sprint');
        }
    };

    const handleStartSprint = async (sprintId) => {
        try {
            await sprintService.startSprint(sprintId);
            message.success('Sprint started!');
            refreshBoard();
        } catch (error) {
            message.error(error.response?.data?.message || 'Failed to start sprint');
        }
    };

    const handleDragEnd = async (result) => {
        const { destination, source, draggableId } = result;

        if (!destination) return;
        if (
            destination.droppableId === source.droppableId &&
            destination.index === source.index
        ) return;

        // Optimistic Update
        const targetSprintId = destination.droppableId === 'backlog' ? null : destination.droppableId;

        // Update local state first for speed
        const updatedTasks = allTasks.map(t => {
            if (t._id === draggableId) {
                return { ...t, sprint: targetSprintId };
            }
            return t;
        });
        setAllTasks(updatedTasks);

        try {
            await taskService.updateTask(draggableId, { sprint: targetSprintId });
            refreshBoard(); // Sync context
        } catch (error) {
            message.error('Failed to move issue');
            loadAllTasks(); // Revert on error
        }
    };

    // Helper to get issues for a specific sprint
    const getSprintIssues = (sprintId) => {
        return allTasks.filter(t => t.sprint && (t.sprint._id === sprintId || t.sprint === sprintId));
    };

    if (!currentProject) return <Empty description="Select a project" />;

    return (
        <div style={{ maxWidth: 1000, margin: '0 auto' }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: 24 }}>
                <div>
                    <Title level={3}>Backlog</Title>
                    <Text type="secondary">{currentProject.name} ({currentProject.key})</Text>
                </div>
                <Button type="primary" onClick={() => setIsCreateSprintModalOpen(true)}>Create Sprint</Button>
            </div>

            <DragDropContext onDragEnd={handleDragEnd}>
                {sprints.map(sprint => (
                    <Card
                        key={sprint._id}
                        style={{ marginBottom: 24, background: colorBgContainer }}
                        bodyStyle={{ padding: 24 }}
                        title={
                            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                                <Space direction="vertical" size={0}>
                                    <Space>
                                        <Text strong>{sprint.name}</Text>
                                        <Tag color={sprint.status === 'active' ? 'green' : 'default'}>{sprint.status}</Tag>
                                    </Space>
                                    <Text type="secondary" style={{ fontSize: 12 }}>
                                        {sprint.startDate ? new Date(sprint.startDate).toLocaleDateString() : 'Planned'} ΓÇó {getSprintIssues(sprint._id).length} issues
                                    </Text>
                                </Space>
                                <Space>
                                    {sprint.status !== 'closed' && (
                                        <Button size="small" onClick={() => handleStartSprint(sprint._id)}>
                                            {sprint.status === 'active' ? 'Complete Sprint' : 'Start Sprint'}
                                        </Button>
                                    )}
                                    <Button type="text" icon={<EllipsisOutlined />} />
                                </Space>
                            </div>
                        }
                    >
                        <Droppable droppableId={sprint._id}>
                            {(provided, snapshot) => (
                                <div
                                    {...provided.droppableProps}
                                    ref={provided.innerRef}
                                    style={{
                                        background: snapshot.isDraggingOver ? '#f4f5f7' : 'transparent',
                                        padding: 8,
                                        minHeight: 50
                                    }}
                                >
                                    {getSprintIssues(sprint._id).map((issue, index) => (
                                        <Draggable key={issue._id} draggableId={issue._id} index={index}>
                                            {(provided) => (
                                                <div
                                                    ref={provided.innerRef}
                                                    {...provided.draggableProps}
                                                    {...provided.dragHandleProps}
                                                    onClick={() => setSelectedIssue(issue)}
                                                    style={{
                                                        padding: '12px 16px',
                                                        marginBottom: 8,
                                                        background: 'white',
                                                        border: '1px solid #dfe1e6',
                                                        borderRadius: 4,
                                                        display: 'flex',
                                                        justifyContent: 'space-between',
                                                        cursor: 'pointer',
                                                        ...provided.draggableProps.style
                                                    }}
                                                >
                                                    <Space>
                                                        <Tag color="blue">{issue.issueType}</Tag>
                                                        <Text type="secondary" style={{ width: 80 }}>{issue.key}</Text>
                                                        <Text>{issue.title}</Text>
                                                    </Space>
                                                    <Space>
                                                        <Tag color={issue.priority === 'high' ? 'red' : 'orange'}>{issue.priority}</Tag>
                                                        <Avatar size="small" style={{ backgroundColor: '#0052CC' }}>{issue.assignedTo?.[0]?.fullName?.[0]}</Avatar>
                                                        <div style={{ background: '#dfe1e6', borderRadius: 10, padding: '2px 8px', fontSize: 12 }}>
                                                            {issue.storyPoints || '-'}
                                                        </div>
                                                    </Space>
                                                </div>
                                            )}
                                        </Draggable>
                                    ))}
                                    {provided.placeholder}
                                </div>
                            )}
                        </Droppable>
                    </Card>
                ))}

                {/* Backlog Section */}
                <Card
                    title={<Text strong>Backlog ({backlogIssues.length} issues)</Text>}
                    bordered={false}
                    style={{ background: 'transparent' }}
                    bodyStyle={{ padding: 0 }}
                >
                    <Droppable droppableId="backlog">
                        {(provided, snapshot) => (
                            <div
                                {...provided.droppableProps}
                                ref={provided.innerRef}
                                style={{
                                    background: snapshot.isDraggingOver ? 'rgba(0,0,0,0.02)' : 'transparent',
                                    padding: 8,
                                    minHeight: 100
                                }}
                            >
                                {backlogIssues.map((issue, index) => (
                                    <Draggable key={issue._id} draggableId={issue._id} index={index}>
                                        {(provided) => (
                                            <div
                                                ref={provided.innerRef}
                                                {...provided.draggableProps}
                                                {...provided.dragHandleProps}
                                                onClick={() => setSelectedIssue(issue)}
                                                style={{
                                                    padding: '12px 16px',
                                                    marginBottom: 8,
                                                    background: 'white',
                                                    border: '1px solid #dfe1e6',
                                                    borderRadius: 4,
                                                    display: 'flex',
                                                    justifyContent: 'space-between',
                                                    cursor: 'pointer',
                                                    ...provided.draggableProps.style
                                                }}
                                            >
                                                <Space>
                                                    <Tag color="blue">{issue.issueType}</Tag>
                                                    <Text type="secondary" style={{ width: 80 }}>{issue.key}</Text>
                                                    <Text>{issue.title}</Text>
                                                </Space>
                                                <Space>
                                                    <Tag color={issue.priority === 'high' ? 'red' : 'orange'}>{issue.priority}</Tag>
                                                    <Avatar size="small" style={{ backgroundColor: '#5E6C84' }}>{issue.assignedTo?.[0]?.fullName?.[0]}</Avatar>
                                                    <div style={{ background: '#dfe1e6', borderRadius: 10, padding: '2px 8px', fontSize: 12 }}>
                                                        {issue.storyPoints || '-'}
                                                    </div>
                                                </Space>
                                            </div>
                                        )}
                                    </Draggable>
                                ))}
                                {provided.placeholder}
                            </div>
                        )}
                    </Droppable>
                </Card>
            </DragDropContext>

            <IssueDetailDrawer
                open={!!selectedIssue}
                issue={selectedIssue}
                onClose={() => {
                    setSelectedIssue(null);
                    loadAllTasks(); // Refresh on close if edits happened
                }}
            />

            <Modal title="Create Sprint" open={isCreateSprintModalOpen} onOk={handleCreateSprint} onCancel={() => setIsCreateSprintModalOpen(false)}>
                <Space direction="vertical" style={{ width: '100%' }}>
                    <Input placeholder="Sprint Name" value={sprintName} onChange={e => setSprintName(e.target.value)} />
                    <Input placeholder="Sprint Goal" value={sprintGoal} onChange={e => setSprintGoal(e.target.value)} />
                </Space>
            </Modal>
        </div>
    );
};

export default Backlog;

import { Drawer, Typography, Descriptions, Tag, Space, Avatar, Divider, Tabs, Button, Input, List, message, Select, Grid } from 'antd';
import {
    CloseOutlined,
    LinkOutlined,
    PaperClipOutlined,
    HistoryOutlined,
    ShareAltOutlined,
    MoreOutlined
} from '@ant-design/icons';
import { useState, useEffect } from 'react';
import taskService from '../../services/taskService';
import { useAuth } from '../../context/AuthContext';

const { Title, Text, Paragraph } = Typography;
const { TextArea } = Input;
const { useBreakpoint } = Grid;

const IssueDetailDrawer = ({ open, onClose, issue }) => {
    const { user } = useAuth();
    const [currentIssue, setCurrentIssue] = useState(issue);
    const [comment, setComment] = useState('');
    const [sending, setSending] = useState(false);
    const screens = useBreakpoint();
    const isMobile = !screens.sm; // < 576px

    useEffect(() => {
        setCurrentIssue(issue);
    }, [issue]);

    if (!currentIssue) return null;

    const handleUpdate = async (field, value) => {
        try {
            const updated = await taskService.updateTask(currentIssue._id, { [field]: value });
            setCurrentIssue(updated);
            message.success('Updated');
        } catch (error) {
            message.error('Update failed');
        }
    };

    const handleAddComment = async () => {
        if (!comment.trim()) return;
        setSending(true);
        try {
            const newComments = await taskService.addComment(currentIssue._id, comment);
            // newComments returns the array of comments usually, depending on controller
            // Controller returns `updatedTask.comments` which is an array
            // Optimization: Update local state
            setCurrentIssue(prev => ({ ...prev, comments: newComments }));
            setComment('');
            message.success('Comment added');
        } catch (error) {
            message.error('Failed to add comment');
        } finally {
            setSending(false);
        }
    };

    const items = [
        {
            label: 'Status',
            children: (
                <Select
                    value={currentIssue.status}
                    onChange={v => handleUpdate('status', v)}
                    options={[
                        { value: 'todo', label: 'To Do' },
                        { value: 'in_progress', label: 'In Progress' },
                        { value: 'review', label: 'Review' },
                        { value: 'done', label: 'Done' }
                    ]}
                    size="small"
                    style={{ width: 120 }}
                />
            )
        },
        {
            label: 'Priority',
            children: (
                <Select
                    value={currentIssue.priority}
                    onChange={v => handleUpdate('priority', v)}
                    options={[
                        { value: 'low', label: 'Low' },
                        { value: 'medium', label: 'Medium' },
                        { value: 'high', label: 'High' },
                        { value: 'critical', label: 'Critical' }
                    ]}
                    size="small"
                    style={{ width: 120 }}
                />
            )
        },
        {
            label: 'Assignee',
            children: <Space>
                <Avatar size="small" style={{ backgroundColor: '#0052CC' }}>{currentIssue.assignedTo?.[0]?.fullName?.[0]}</Avatar>
                {currentIssue.assignedTo?.[0]?.fullName || 'Unassigned'}
            </Space>
        },
        {
            label: 'Reporter',
            children: <Space>
                <Avatar size="small" style={{ backgroundColor: '#5E6C84' }}>{currentIssue.reporter?.fullName?.[0]}</Avatar>
                {currentIssue.reporter?.fullName || 'Unknown'}
            </Space>
        },
        {
            label: 'Sprint',
            children: currentIssue.sprint?.name || 'Backlog'
        },
        {
            label: 'Story Points',
            children: <Input
                style={{ width: 60 }}
                defaultValue={currentIssue.storyPoints}
                onBlur={e => {
                    if (e.target.value !== String(currentIssue.storyPoints)) {
                        handleUpdate('storyPoints', Number(e.target.value));
                    }
                }}
                size="small"
            />
        },
    ];

    const timelineItems = (currentIssue.history || []).map(h => ({
        label: new Date(h.timestamp).toLocaleString(),
        children: `${h.updatedBy?.fullName || 'User'} changed ${h.field} from "${h.oldValue}" to "${h.newValue}"`
    })).reverse(); // Newest first

    return (
        <Drawer
            title={
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', width: '100%' }}>
                    <Space>
                        <Text type="secondary">{currentIssue.key}</Text>
                        <LinkOutlined />
                    </Space>
                    <Space>
                        <Button icon={<ShareAltOutlined />}>Share</Button>
                        <Button icon={<MoreOutlined />} />
                        <Button type="text" icon={<CloseOutlined />} onClick={onClose} />
                    </Space>
                </div>
            }
            placement="right"
            width={isMobile ? '100%' : 600}
            onClose={onClose}
            open={open}
            closable={false}
            mask={false}
            headerStyle={{ padding: '16px 24px' }}
        >
            <div style={{ paddingBottom: 24 }}>
                <Title level={4} editable={{ onChange: v => handleUpdate('title', v) }}>{currentIssue.title}</Title>

                <Space style={{ marginBottom: 24 }}>
                    <Button icon={<PaperClipOutlined />}>Attach</Button>
                    <Button icon={<LinkOutlined />}>Link Issue</Button>
                </Space>

                <div style={{ display: 'flex', gap: 24, flexDirection: 'column' }}>
                    <div style={{ flex: 2 }}>
                        <Title level={5}>Description</Title>
                        <Paragraph
                            editable={{ onChange: v => handleUpdate('description', v) }}
                            style={{ minHeight: 60, color: '#172b4d', whiteSpace: 'pre-wrap' }}
                        >
                            {currentIssue.description || 'Click to add description...'}
                        </Paragraph>

                        <Tabs defaultActiveKey="1" items={[
                            {
                                key: '1',
                                label: 'Comments',
                                children: (
                                    <>
                                        <div style={{ display: 'flex', gap: 12, marginBottom: 16 }}>
                                            <Avatar>{user?.fullName?.[0]}</Avatar>
                                            <div style={{ flex: 1 }}>
                                                <TextArea
                                                    placeholder="Add a comment..."
                                                    autoSize={{ minRows: 2, maxRows: 6 }}
                                                    value={comment}
                                                    onChange={e => setComment(e.target.value)}
                                                />
                                                {comment && (
                                                    <div style={{ marginTop: 8 }}>
                                                        <Button type="primary" size="small" onClick={handleAddComment} loading={sending} style={{ marginRight: 8 }}>Save</Button>
                                                        <Button size="small" type="text" onClick={() => setComment('')}>Cancel</Button>
                                                    </div>
                                                )}
                                            </div>
                                        </div>
                                        <List
                                            itemLayout="horizontal"
                                            dataSource={currentIssue.comments || []}
                                            renderItem={(item) => (
                                                <List.Item>
                                                    <List.Item.Meta
                                                        avatar={<Avatar size="small">{item.user?.fullName?.[0]}</Avatar>}
                                                        title={<Space><Text strong>{item.user?.fullName}</Text><Text type="secondary" style={{ fontSize: 12 }}>{new Date(item.timestamp).toLocaleString()}</Text></Space>}
                                                        description={item.text}
                                                    />
                                                </List.Item>
                                            )}
                                        />
                                    </>
                                ),
                            },
                            {
                                key: '2',
                                label: 'History',
                                children: (
                                    (currentIssue.history && currentIssue.history.length > 0) ?
                                        <List
                                            size="small"
                                            dataSource={timelineItems}
                                            renderItem={item => (
                                                <List.Item>
                                                    <Text type="secondary" style={{ fontSize: 12 }}>{item.label}</Text> <br />
                                                    <Text>{item.children}</Text>
                                                </List.Item>
                                            )}
                                        /> :
                                        <div style={{ padding: 16, textAlign: 'center', color: '#999' }}>No history yet</div>
                                )
                            }
                        ]} />
                    </div>

                    <Divider />

                    <div style={{ flex: 1 }}>
                        <Descriptions column={2} layout="vertical" items={items} size="small" />
                        <Divider />
                        <div style={{ fontSize: 12, color: '#6B778C' }}>
                            <Text type="secondary" style={{ display: 'block', marginBottom: 4 }}>Created {new Date(currentIssue.createdAt).toLocaleDateString()}</Text>
                            <Text type="secondary">Updated {new Date(currentIssue.updatedAt).toLocaleDateString()}</Text>
                        </div>
                    </div>
                </div>
            </div>
        </Drawer>
    );
};

export default IssueDetailDrawer;

import { createContext, useState, useEffect, useContext, useCallback } from 'react';
import api from '../services/api';

const AuthContext = createContext(null);

export const AuthProvider = ({ children }) => {
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const checkLoggedIn = async () => {
            const token = localStorage.getItem('token');
            if (token) {
                try {
                    // In a real app, you'd call an endpoint like /auth/me
                    // For now, we'll try to persist the user if we saved it in localStorage
                    // Or ideally, we should decode the token if it contains user info.

                    // Let's assume we save user info in localStorage alongside token for now
                    const storedUser = localStorage.getItem('user');
                    if (storedUser) {
                        setUser(JSON.parse(storedUser));
                    }
                } catch (error) {
                    console.error("Auth check failed", error);
                    localStorage.removeItem('token');
                    localStorage.removeItem('user');
                }
            }
            setLoading(false);
        };
        checkLoggedIn();
    }, []);

    const login = async (email, password) => {
        try {
            const res = await api.post('/auth/login', { email, password });
            const { token, ...userData } = res.data;
            localStorage.setItem('token', token);
            localStorage.setItem('user', JSON.stringify(userData)); // Save user data
            setUser(userData);
            return userData;
        } catch (error) {
            throw error.response?.data?.message || 'Login failed';
        }
    };

    const register = async (userData) => {
        try {
            const res = await api.post('/auth/register', userData);
            const { token, ...user } = res.data;
            localStorage.setItem('token', token);
            localStorage.setItem('user', JSON.stringify(user)); // Save user data
            setUser(user);
            return user;
        } catch (error) {
            throw error.response?.data?.message || 'Registration failed';
        }
    };

    const logout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        setUser(null);
    };

    const setUserFromGoogle = useCallback((userData) => {
        setUser(userData);
    }, []);

    return (
        <AuthContext.Provider value={{ user, login, register, logout, loading, setUserFromGoogle }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => useContext(AuthContext);

