export default LoginPage;

import { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import {
    Box, Button, TextField, Typography, Container, Paper,
    Alert, CircularProgress, InputAdornment, IconButton, MenuItem
} from '@mui/material';
import { Email, Lock, Person, Visibility, VisibilityOff, Business, Work } from '@mui/icons-material';
import { toast } from 'react-toastify';

const RegisterPage = () => {
    const [formData, setFormData] = useState({
        fullName: '',
        email: '',
        password: '',
        confirmPassword: '',
        role: 'technical_team',
        department: ''
    });
    const [showPassword, setShowPassword] = useState(false);
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);

    const { register } = useAuth();
    const navigate = useNavigate();

    const roles = [
        { value: 'admin', label: 'Administrator' },
        { value: 'project_manager', label: 'Project Manager' },
        { value: 'technical_team', label: 'Technical Team' },
        { value: 'marketing_team', label: 'Marketing Team' },
        { value: 'technical_lead', label: 'Technical Lead' },
        { value: 'marketing_lead', label: 'Marketing Lead' },
    ];

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError('');

        if (formData.password !== formData.confirmPassword) {
            setError('Passwords do not match');
            return;
        }

        setLoading(true);
        try {
            const { confirmPassword, ...userData } = formData;
            await register(userData);
            toast.success('Registration Successful!');
            navigate('/dashboard');
        } catch (err) {
            setError(err);
            toast.error(err);
        } finally {
            setLoading(false);
        }
    };

    return (
        <Box
            sx={{
                minHeight: '100vh',
                display: 'flex',
                background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                position: 'relative',
                overflow: 'hidden',
                py: 4,
                '&::before': {
                    content: '""',
                    position: 'absolute',
                    top: '-50%',
                    right: '-50%',
                    width: '100%',
                    height: '100%',
                    background: 'radial-gradient(circle, rgba(255,255,255,0.1) 0%, transparent 70%)',
                    animation: 'float 20s ease-in-out infinite',
                },
                '@keyframes float': {
                    '0%, 100%': { transform: 'translate(0, 0) rotate(0deg)' },
                    '50%': { transform: 'translate(30px, 30px) rotate(180deg)' },
                }
            }}
        >
            <Container component="main" maxWidth="sm" sx={{ display: 'flex', alignItems: 'center', zIndex: 1 }}>
                <Paper
                    elevation={24}
                    sx={{
                        p: 5,
                        width: '100%',
                        borderRadius: 4,
                        background: 'rgba(255, 255, 255, 0.95)',
                        backdropFilter: 'blur(10px)',
                        boxShadow: '0 8px 32px rgba(0, 0, 0, 0.1)',
                        transition: 'transform 0.3s ease',
                        '&:hover': {
                            transform: 'translateY(-5px)',
                        }
                    }}
                >
                    {/* Logo & Title */}
                    <Box sx={{ textAlign: 'center', mb: 3 }}>
                        <Box
                            sx={{
                                display: 'inline-flex',
                                p: 2,
                                borderRadius: '50%',
                                background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                                mb: 2,
                            }}
                        >
                            <Business sx={{ fontSize: 40, color: 'white' }} />
                        </Box>
                        <Typography
                            variant="h4"
                            sx={{
                                fontWeight: 700,
                                background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                                backgroundClip: 'text',
                                WebkitBackgroundClip: 'text',
                                WebkitTextFillColor: 'transparent',
                                mb: 0.5
                            }}
                        >
                            Join Digital Dockers
                        </Typography>
                        <Typography variant="body2" color="text.secondary">
                            Create your account to get started
                        </Typography>
                    </Box>

                    {error && (
                        <Alert severity="error" sx={{ mb: 2, borderRadius: 2 }}>
                            {error}
                        </Alert>
                    )}

                    <Box component="form" onSubmit={handleSubmit} noValidate>
                        <TextField
                            margin="dense"
                            required
                            fullWidth
                            name="fullName"
                            label="Full Name"
                            value={formData.fullName}
                            onChange={handleChange}
                            InputProps={{
                                startAdornment: (
                                    <InputAdornment position="start">
                                        <Person color="action" />
                                    </InputAdornment>
                                ),
                            }}
                            sx={{ mb: 1.5, '& .MuiOutlinedInput-root': { borderRadius: 2 } }}
                        />

                        <TextField
                            margin="dense"
                            required
                            fullWidth
                            name="email"
                            label="Email Address"
                            type="email"
                            value={formData.email}
                            onChange={handleChange}
                            InputProps={{
                                startAdornment: (
                                    <InputAdornment position="start">
                                        <Email color="action" />
                                    </InputAdornment>
                                ),
                            }}
                            sx={{ mb: 1.5, '& .MuiOutlinedInput-root': { borderRadius: 2 } }}
                        />

                        <TextField
                            margin="dense"
                            required
                            fullWidth
                            name="password"
                            label="Password"
                            type={showPassword ? 'text' : 'password'}
                            value={formData.password}
                            onChange={handleChange}
                            InputProps={{
                                startAdornment: (
                                    <InputAdornment position="start">
                                        <Lock color="action" />
                                    </InputAdornment>
                                ),
                                endAdornment: (
                                    <InputAdornment position="end">
                                        <IconButton onClick={() => setShowPassword(!showPassword)} edge="end">
                                            {showPassword ? <VisibilityOff /> : <Visibility />}
                                        </IconButton>
                                    </InputAdornment>
                                ),
                            }}
                            sx={{ mb: 1.5, '& .MuiOutlinedInput-root': { borderRadius: 2 } }}
                        />

                        <TextField
                            margin="dense"
                            required
                            fullWidth
                            name="confirmPassword"
                            label="Confirm Password"
                            type={showPassword ? 'text' : 'password'}
                            value={formData.confirmPassword}
                            onChange={handleChange}
                            InputProps={{
                                startAdornment: (
                                    <InputAdornment position="start">
                                        <Lock color="action" />
                                    </InputAdornment>
                                ),
                            }}
                            sx={{ mb: 1.5, '& .MuiOutlinedInput-root': { borderRadius: 2 } }}
                        />

                        <TextField
                            margin="dense"
                            required
                            fullWidth
                            select
                            name="role"
                            label="Role"
                            value={formData.role}
                            onChange={handleChange}
                            InputProps={{
                                startAdornment: (
                                    <InputAdornment position="start">
                                        <Work color="action" />
                                    </InputAdornment>
                                ),
                            }}
                            sx={{ mb: 1.5, '& .MuiOutlinedInput-root': { borderRadius: 2 } }}
                        >
                            {roles.map((option) => (
                                <MenuItem key={option.value} value={option.value}>
                                    {option.label}
                                </MenuItem>
                            ))}
                        </TextField>

                        <TextField
                            margin="dense"
                            fullWidth
                            name="department"
                            label="Department (Optional)"
                            value={formData.department}
                            onChange={handleChange}
                            sx={{ mb: 2, '& .MuiOutlinedInput-root': { borderRadius: 2 } }}
                        />

                        <Button
                            type="submit"
                            fullWidth
                            variant="contained"
                            disabled={loading}
                            sx={{
                                py: 1.5,
                                mb: 2,
                                borderRadius: 2,
                                fontSize: '1rem',
                                fontWeight: 600,
                                background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                                boxShadow: '0 4px 15px rgba(102, 126, 234, 0.4)',
                                transition: 'all 0.3s',
                                '&:hover': {
                                    background: 'linear-gradient(135deg, #764ba2 0%, #667eea 100%)',
                                    boxShadow: '0 6px 20px rgba(102, 126, 234, 0.6)',
                                    transform: 'translateY(-2px)',
                                },
                            }}
                        >
                            {loading ? <CircularProgress size={24} color="inherit" /> : 'Create Account'}
                        </Button>

                        <Box sx={{ textAlign: 'center' }}>
                            <Link to="/login" style={{ textDecoration: 'none' }}>
                                <Typography
                                    variant="body2"
                                    sx={{
                                        color: '#667eea',
                                        fontWeight: 500,
                                        transition: 'all 0.3s',
                                        '&:hover': {
                                            color: '#764ba2',
                                            textDecoration: 'underline',
                                        }
                                    }}
                                >
                                    Already have an account? Sign In
                                </Typography>
                            </Link>
                        </Box>
                    </Box>
                </Paper>
            </Container>
        </Box>
    );
};

export default RegisterPage;

import { Box, Button, Container, Grid, Typography, useTheme, useMediaQuery, AppBar, Toolbar } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { Edit, Chat, People, ArrowForward, Business } from '@mui/icons-material';
import { motion, useScroll, useTransform } from 'framer-motion';
import GlassCard from '../../components/common/GlassCard';

const LandingPage = () => {
    const navigate = useNavigate();
    const theme = useTheme();
    const isMobile = useMediaQuery(theme.breakpoints.down('md'));
    const { scrollY } = useScroll();
    const yHero = useTransform(scrollY, [0, 500], [0, 150]);

    const features = [
        {
            icon: <Edit fontSize="large" sx={{ color: '#4f46e5' }} />,
            title: 'AI Meeting Summaries',
            description: 'Automatically transcribe and summarize your Google Meets. Never miss a detail.'
        },
        {
            icon: <Chat fontSize="large" sx={{ color: '#ec4899' }} />,
            title: 'Real-time Collaboration',
            description: 'Instant chat and direct messaging for seamless team communication.'
        },
        {
            icon: <People fontSize="large" sx={{ color: '#8b5cf6' }} />,
            title: 'Visual Organization',
            description: 'Interactive organization charts and task visualization for clear hierarchy.'
        }
    ];

    return (
        <Box sx={{ minHeight: '100vh', bgcolor: 'background.default', overflowX: 'hidden' }}>
            {/* Transparent Glass Header */}
            <AppBar position="fixed" elevation={0} sx={{
                background: 'rgba(255,255,255,0.05)',
                backdropFilter: 'blur(10px)',
                borderBottom: '1px solid rgba(255,255,255,0.1)'
            }}>
                <Container maxWidth="lg">
                    <Toolbar disableGutters sx={{ justifyContent: 'space-between' }}>
                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                            <Business sx={{ color: '#4f46e5', fontSize: 32 }} />
                            <Typography variant="h6" fontWeight={700} sx={{ color: '#0f172a' }}>
                                Digital Dockers
