                            <Box
                                component="img"
                                src="/dashboard_preview.png"
                                alt="Digital Dockers Dashboard"
                                sx={{
                                    width: '100%',
                                    height: 'auto',
                                    display: 'block',
                                    transform: 'scale(1.0)',
                                    transition: 'transform 0.5s ease',
                                    '&:hover': { transform: 'scale(1.02)' }
                                }}
                            />

                            {/* Glass Overlay Effect */}
                            <Box
                                sx={{
                                    position: 'absolute',
                                    inset: 0,
                                    background: 'linear-gradient(180deg, rgba(255,255,255,0) 0%, rgba(15, 23, 42, 0.1) 100%)',
                                    pointerEvents: 'none'
                                }}
                            />
                        </Box>
                    </motion.div>
                </Container>
            </Box>

            {/* Features Section */}
            <Box id="features" sx={{ py: 10, bgcolor: 'transparent' }}>
                <Container maxWidth="lg">
                    <Typography variant="h3" align="center" fontWeight={700} sx={{ mb: 8 }}>
                        Why Digital Dockers?
                    </Typography>
                    <Grid container spacing={4}>
                        {features.map((feature, idx) => (
                            <Grid item xs={12} md={4} key={idx}>
                                <motion.div
                                    initial={{ opacity: 0, y: 50 }}
                                    whileInView={{ opacity: 1, y: 0 }}
                                    transition={{ duration: 0.5, delay: idx * 0.1 }}
                                    viewport={{ once: true }}
                                    style={{ height: '100%' }}
                                >
                                    <GlassCard sx={{ p: 4, height: '100%', display: 'flex', flexDirection: 'column', alignItems: 'center', textAlign: 'center' }}>
                                        <Box sx={{
                                            p: 2,
                                            borderRadius: 2,
                                            bgcolor: 'rgba(79, 70, 229, 0.1)',
                                            mb: 3,
                                            display: 'inline-flex'
                                        }}>
                                            {feature.icon}
                                        </Box>
                                        <Typography variant="h5" fontWeight={600} gutterBottom>
                                            {feature.title}
                                        </Typography>
                                        <Typography variant="body1" color="text.secondary">
                                            {feature.description}
                                        </Typography>
                                    </GlassCard>
                                </motion.div>
                            </Grid>
                        ))}
                    </Grid>
                </Container>
            </Box>

            {/* Footer */}
            <Box sx={{ py: 6, borderTop: '1px solid', borderColor: 'divider', bgcolor: 'white' }}>
                <Container maxWidth="lg" sx={{ textAlign: 'center' }}>
                    <Typography variant="body2" color="text.secondary">
                        ┬⌐ 2024 Digital Dockers. Built with AI.
                    </Typography>
                </Container>
            </Box>
        </Box>
    );
};

